import React, { Component } from 'react'
import './Tasks.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import axios from 'axios';
import PropTypes from "prop-types";
import { BrowserRouter as Router, Route, Link} from 'react-router-dom';


export class Tasks extends Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: []
        };
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));

            console.log(this.state);
    }

    deleteTask(data){
        axios.post('/api/deleteTask', {
            id: data
          })
          .then((response) => {
            console.log(data);
          }, (error) => {
            console.log(error);
          });
    }

    render() {

        return (
            <div className="container" >

                <h1 className="text-center p-4">Tasks</h1>
                <table  className='table table-striped table-bordered table_tasks'>
                    <thead className="thead-dark ">
                        <tr>
                            <th>#</th>
                            <th>Task Name</th>
                            <th>Created date</th>
                            <th>Created by</th>
                            <th>Preview</th>
                            <th>Edit</th>
                            <th>Done</th>
                            <th>Delete</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        
                        {this.state.tasks.map((task,i) => {
                            return(
                            <tr key={task.id} >
                                <td> { i } </td>
                                <td> { task.name } </td>
                                <td> { task.date.toLocaleString() } </td>
                                <td> { task.created_by } </td>
                                <td >
                                    <Link to={`/previewtask/${task.id}`} >
                                        <i className="fa fa-eye fa-2x  privew" aria-hidden="true"></i>
                                    </Link>
                                </td>
                                <td >
                                
                                    <Link to={`/edittask/${task.id}`} >
                                        <i className="fa fa-pencil-square-o fa-2x" aria-hidden="true"></i>
                                    </Link>
                                </td>
                                <td > { task.done == false ? <i className="fa fa-2x fa-times-circle text-danger" aria-hidden="true"></i> : <i className="fa fa-2x text-success fa-check-circle" aria-hidden="true"></i>  }</td>
                                <td>
                                    <i onClick={this.deleteTask.bind(this, task.id)} class="fa fa-trash fa-2x text-danger" aria-hidden="true"></i>
                                </td>
                            </tr>
                            )
                        }

                                 
                        )}
                    </tbody>
                </table>
            </div>
        );
    }
}
const taskHeader = {
    color: 'rgb(51, 51, 51)',
}
const containerStyle = {
    maxWidth: '1440px',
    paddingRight: '15px',
    paddingleft: '15px',
    marginright: 'auto',
    marginLeft: 'auto'
}



export default Tasks;
