import React, { Component } from 'react'
import './Tasks.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form,Button, Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import {Link} from 'react-router-dom';


export class Tasks extends Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: []
        };
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));
    }

    deleteTask(data){
        axios.post('/api/deleteTask', {
            id: data
          })
          .then((response) => {
            window.location.reload();
          }, (error) => {
            console.log(error);
          });
    }

    render() {

        return (
            <div className="container" >

                <h1 className="text-center p-4">Tasks</h1>
                <Link to="/createnewtask"><Button className="text-right m-3" variant="primary" type="submit">Create new task</Button></Link>
                <div>
                    <Row className="bg-info text-white">
                        <Col md="auto" className="d-none d-md-flex text-center p-3 font-weight-bold">#</Col>
                        <Col xs="8" className="text-left p-3 font-weight-bold">Task Name</Col>
                        <Col  className="d-none d-md-flex text-center p-3 font-weight-bold">Created date</Col>
                        <Col xs="1" md="1" className="d-none d-md-flex text-center p-3 font-weight-bold">Preview</Col>
                        <Col xs="1" md="1" className="text-center p-3 font-weight-bold">Edit</Col>
                        <Col xs="1" md="1" className="text-center p-3 font-weight-bold">Done</Col>
                        <Col xs="1" md="1" className="text-center p-3 font-weight-bold">Delete</Col>
                    </Row>
                    {this.state.tasks.map((task,i) => {
                                return(
                                    <Row className="align-items-center">
                                        <Col md="auto" className="fields_css d-none d-md-flex p-3 font-weight-bold ">#{ i + 1 }</Col>
                                        <Col   className="fields_css text-left p-3"> { task.name }</Col>
                                        <Col  className="fields_css p-3 d-none d-md-flex text-center">{ task.date.toLocaleString() }</Col>

                                        <Col md="1" className=" p-3 d-none d-md-inline text-center">
                                            <Link to={`/previewtask/${task.id}`} >
                                                <i className="icons_css fa fa-eye fa-2x  privew" aria-hidden="true"></i>
                                            </Link>
                                        </Col>

                                        <Col md="1" className="  p-3 text-center">                                    
                                            <Link to={`/edittask/${task.id}`} >
                                                <i className="icons_css fa fa-pencil-square-o fa-2x text-warning" aria-hidden="true"></i>
                                            </Link>
                                        </Col>

                                        <Col md="1" className=" p-3 text-center"> { !task.done ? <i className="privew icons_css fa fa-2x fa-times-circle text-danger"  aria-hidden="true"></i> : <i className=" privew icons_css fa fa-2x text-success fa-check-circle" aria-hidden="true"></i>  }</Col>

                                        <Col md="1" className=" p-3 text-center"><i style={{cursor:'pointer'}} onClick={this.deleteTask.bind(this, task.id)} className="icons_css fa fa-trash fa-2x text-danger"  aria-hidden="true"></i></Col>
                                    </Row>
                    )})}
               </div>
            </div>
        );
    }
}



export default Tasks;
