import React, { Component } from 'react'
import './Tasks.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {EditTask} from './EditTask';

export class Tasks extends Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: [],
            addModalShow : false,
        };
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));
    }

    render() {
        let addModalClose = () => this.setState({addModalShow:false});
        return (
            <div className="container" >

                <h1 className="text-center p-4">Tasks</h1>
                <table className='table table-striped table-bordered'>
                    <thead className="thead-dark">
                        <tr>
                            <th>#</th>
                            <th>Task Name</th>
                            <th>Created date</th>
                            <th>Created by</th>
                            <th>Preview</th>
                            <th>Done</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        
                        {this.state.tasks.map((task,i) => {
                            return(
                            <tr key={task.id} >
                                <td> { i } </td>
                                <td> { task.name } </td>
                                <td> { task.date.toLocaleString() } </td>
                                <td> { task.created_by } </td>
                                <td> <i className="fa fa-eye fa-2x" aria-hidden="true"></i></td>
                                <td className="text-center"> { task.done == false ? <i className="fa fa-2x fa-times-circle text-danger" aria-hidden="true"></i> : <i className="fa fa-2x text-success fa-check-circle" aria-hidden="true"></i>  }</td>
                                <td>
                                </td>
                            </tr>
                            )
                        }

                                 
                        )}
                    </tbody>
                </table>
            </div>
        );
    }
}
const taskHeader = {
    color: 'rgb(51, 51, 51)',
}
const containerStyle = {
    maxWidth: '1440px',
    paddingRight: '15px',
    paddingleft: '15px',
    marginright: 'auto',
    marginLeft: 'auto'
}



export default Tasks;
