import React, { Component } from 'react'

export class Tasks extends Component {

    constructor(){
        super();
        this.state = {
            customers: []
        }
    }
    
    componentDidMount(){
        fetch('/api/customers')
            .then(res => res.json())
            .then(customers => this.setState({customers} , () => console.log('customer fethed...' , customers) ));
    }

    render() {
        return (
            <div>
                <h1>Customers</h1>
                <ul>
                    {this.state.customers.map(customers =>
                    <li key={customers.id}> { customers.firstName } </li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Tasks;
