import React, { Component } from 'react'
import './Tasks.css';

export class Tasks extends Component {

    constructor(){
        super();
        this.state = {
            tasks: []
        };
        this.countRows = 0;
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));
    }

    render() {
        return (
            <div className="container" >

                <h1>Taks</h1>
                <table className='table table-striped table-bordered'>
                    <thead className="thead-dark">
                        <th className='col'>#</th>
                        <th className='col'>Task Name</th>
                        <th className='col'>Created date</th>
                        <th className='col'>Show</th>
                        <th className='col'>Edit</th>
                        <th className='col'>Delete</th>
                        <th className='col'>Created by</th>
                    </thead>
                    <tbody>
                        
                        {this.state.tasks.map((task,i) => {
                            return(
                            <tr key={task.id} >
                                <td> { i } </td>
                                <td> { task.name } </td>
                                <td> { DATE_FORMAT(task.date, "%Y-%m-%d") } </td>
                            </tr>
                            )
                        }

                                 
                        )}
                    </tbody>
                </table>
            </div>
        );
    }
}
const taskHeader = {
    color: 'rgb(51, 51, 51)',
}
const containerStyle = {
    maxWidth: '1440px',
    paddingRight: '15px',
    paddingleft: '15px',
    marginright: 'auto',
    marginLeft: 'auto'
}



export default Tasks;
