import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form,Button, Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import { Link } from 'react-router-dom';


export class EditTask extends Component {

    constructor(props){
        super(props);
        this.state = {
            id: '',
            name: '',
            created_by: '',
            done: ''
        };
        
    }

    componentDidMount(){
       this.getTaskById();
    }

    getTaskById(){
        const { match: { params } } = this.props;
        axios.get(`/api/getTaskById/?id=${params.userId}`)
        .then(response => {

                this.setState({ 
                    id: response.data[0].id,
                    name: response.data[0].name,
                    created_by: response.data[0].created_by,
                    done: response.data[0].done
                }, () =>{
                    console.log(this.state);
                })
        })
    }


    render() {
     
        return (
            
            <Container>
                <h1 className="text-center p-4" >Edit Task</h1>

                <Form>
                    <Form.Group as={Row} controlId="formPlaintextEmail">
                        <Form.Label column sm="2">
                            Task Name:
                        </Form.Label>
                        <Col sm="10">
                            <Form.Control  defaultValue={this.state.name} />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formPlaintextPassword">
                        <Form.Label column sm="2">
                        Password
                        </Form.Label>
                        <Col sm="10">
                        <Form.Control type="password" placeholder="Password" />
                        </Col>
                    </Form.Group>
                    <Row>
                        <Col>
                            <Button  className=""  variant="secondary">
                                <Link className="text-white" to="/">Back</Link>    
                            </Button>
                        </Col>

                        <Col>                    
                            <Button className="text-right" variant="primary" type="submit">
                                Submit
                            </Button>
                        </Col>

                    </Row>



                </Form>
                
            </Container>
        )
    }
}

export default EditTask
