import React, { Component } from 'react'
import './Tasks.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form,Button, Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import {Link} from 'react-router-dom';


export class Tasks extends Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: []
        };
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));
    }

    deleteTask(data){
        axios.post('/api/deleteTask', {
            id: data
          })
          .then((response) => {
            window.location.reload();
          }, (error) => {
            console.log(error);
          });
    }

    render() {

        return (
            <div className="container" >

                <h1 className="text-center p-4">Tasks</h1>
                <Link to="/createnewtask"><Button className="text-right m-3" variant="primary" type="submit">Create new task</Button></Link>
                <Row>
                    <Col className="d-none d-md-flex">#</Col>
                    <Col xs="5">Task Name</Col>
                    <Col className="d-none d-md-flex">Created date</Col>
                    <Col className="d-none d-md-flex">Preview</Col>
                    <Col>Edit</Col>
                    <Col>Done</Col>
                    <Col>Delete</Col>
                </Row>
                {this.state.tasks.map((task,i) => {
                            return(
                                <Row>
                                    <Col className="d-none d-md-flex">{ i + 1 }</Col>
                                    <Col xs="5"> { task.name }</Col>
                                    <Col className="d-none d-md-flex">{ task.date.toLocaleString() }</Col>

                                    <Col className="d-none d-md-flex">
                                        <Link to={`/previewtask/${task.id}`} >
                                            <i className="icons_css fa fa-eye fa-2x  privew" aria-hidden="true"></i>
                                        </Link>
                                    </Col>

                                    <Col>                                    
                                        <Link to={`/edittask/${task.id}`} >
                                            <i className="icons_css fa fa-pencil-square-o fa-2x text-warning" aria-hidden="true"></i>
                                        </Link>
                                    </Col>

                                    <Col> { !task.done ? <i className="privew icons_css fa fa-2x fa-times-circle text-danger"  aria-hidden="true"></i> : <i className=" privew icons_css fa fa-2x text-success fa-check-circle" aria-hidden="true"></i>  }</Col>

                                    <Col><i style={{cursor:'pointer'}} onClick={this.deleteTask.bind(this, task.id)} className="icons_css fa fa-trash fa-2x text-danger"  aria-hidden="true"></i></Col>
                                </Row>
                )})}
               
            </div>
        );
    }
}



export default Tasks;
