import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form,Button, Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import './EditTask.css';
import { BrowserRouter as Router, Route, Link} from 'react-router-dom';






export class EditTask extends Component {

    constructor(props){
        super(props);
        this.state = {
            redirect: false,
            id: '',
            name: '',
            text: '',
            date: '',
            created_by: '',
            done: ''
        };
        
    }

    componentDidMount(){
       this.getTaskById();
    }

    getTaskById(){
        const { match: { params } } = this.props;
        axios.get(`/api/getTaskById/?id=${params.userId}`)
        .then(response => {

                this.setState({ 
                    id: response.data[0].id,
                    name: response.data[0].name,
                    text: response.data[0].text,
                    date: response.data[0].date,
                    created_by: response.data[0].created_by,
                    done: response.data[0].done
                }, () =>{
                    console.log(this.state);
                })
        })
    }

    markAsNotDone(){
        const { match: { params } } = this.props;
        axios.get(`/api/markAsNotDone/?id=${params.userId}`)
        .then(response => {
            
        }).catch(err => console.log(err));
       
    }

      markAsDone(){
        const { match: { params } } = this.props;
        axios.get(`/api/markAsDone/?id=${params.userId}`)
        .then(response => {
            this.setState({ redirect: true });
        }).catch(err => console.log(err));
    }

    render() {


        return (
           

            
            <Container>
                <h1 className="text-center p-4" >Edit Task</h1>
                <div className="wrap_form">
                    <Form>
                        <Row>
                            <Col>
                                {
                                this.state.done == true ? 
                                <Button onClick={this.markAsNotDone.bind(this)} className="float-right mb-3" variant="danger" type="submit">
                                    Mark as not completed
                                </Button>
                                : 
                                <Button onClick={this.markAsDone.bind(this)} className="float-right mb-3" variant="success" type="submit">
                                    Mark as not completed
                                </Button>
                                }                    

                            </Col>
                            
                        </Row>

                        <Form.Group as={Row} controlId="formPlaintextEmail">
                            <Form.Label column sm="2">
                                Task name:
                            </Form.Label>
                            <Col sm="10">
                                <Form.Control placeholder="Enter task name...."  defaultValue={this.state.name} />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">Task content:</Form.Label>
                            <Col sm="10">
                                <Form.Control type="textarea" placeholder="Enter content...." defaultValue={this.state.text} />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextEmail">
                            <Form.Label column sm="2">Created by:</Form.Label>
                            <Col sm="10">
                            <Form.Control plaintext readOnly defaultValue={this.state.created_by} />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextEmail">
                            <Form.Label column sm="2">Date created:</Form.Label>
                            <Col sm="10">
                            <Form.Control plaintext readOnly defaultValue={this.state.date} />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">Task done:</Form.Label>
                            <Col sm="10">
                            {this.state.done == true ? <i className="fa fa-2x text-success fa-check-circle" aria-hidden="true"></i> : <i className="fa fa-2x fa-times-circle text-danger" aria-hidden="true"></i>}
                            </Col>
                        </Form.Group>

                        <Row>
                            <Col>
                                <Button  className=""  variant="secondary">
                                    <Link className="text-white" to="/">Back</Link>    
                                </Button>
                            </Col>

                            <Col>                    
                                <Button className="float-right" variant="primary" type="submit">
                                    Submit
                                </Button>
                            </Col>
                            
                        </Row>

                    </Form>
                </div>
                
            </Container>
        )
    }
}

export default EditTask
