import React, { Component } from 'react'
import './Tasks.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form,Button, Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import {Link} from 'react-router-dom';


export class Tasks extends Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: []
        };
    }
    
    componentDidMount(){
        fetch('/api/getAllTasks')
            .then(res => res.json())
            .then(tasks => this.setState({tasks} , () => console.log('customer fethed...' , tasks) ));
    }

    deleteTask(data){
        axios.post('/api/deleteTask', {
            id: data
          })
          .then((response) => {
            window.location.reload();
          }, (error) => {
            console.log(error);
          });
    }

    render() {

        return (
            <div className="container" >

                <h1 className="text-center p-4">Tasks</h1>
                <Link to="/createnewtask"><Button className="float-right m-3" variant="primary" type="submit">Create new task</Button></Link>
                <table  className='table table-striped table-bordered table_tasks'>
                    <thead className="thead-dark ">
                        <tr>
                            <th>#</th>
                            <th>Task Name</th>
                            <th>Created date</th>
                            <th>Preview</th>
                            <th>Edit</th>
                            <th>Done</th>
                            <th>Delete</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        
                        {this.state.tasks.map((task,i) => {
                            return(
                            <tr key={task.id} >
                                <td> { i + 1 } </td>
                                <td> { task.name } </td>
                                <td> { task.date.toLocaleString() } </td>
                                <td >
                                    <Link to={`/previewtask/${task.id}`} >
                                        <i className="icons_css fa fa-eye fa-2x  privew" aria-hidden="true"></i>
                                    </Link>
                                </td>
                                <td >
                                
                                    <Link to={`/edittask/${task.id}`} >
                                        <i className="icons_css fa fa-pencil-square-o fa-2x text-warning" aria-hidden="true"></i>
                                    </Link>
                                </td>
                                <td > { !task.done ? <i className="privew icons_css fa fa-2x fa-times-circle text-danger"  aria-hidden="true"></i> : <i className=" privew icons_css fa fa-2x text-success fa-check-circle" aria-hidden="true"></i>  }</td>
                                <td>
                                    <i style={{cursor:'pointer'}} onClick={this.deleteTask.bind(this, task.id)} className="icons_css fa fa-trash fa-2x text-danger"  aria-hidden="true"></i>
                                </td>
                            </tr>
                            )
                        }

                                 
                        )}
                    </tbody>
                </table>
            </div>
        );
    }
}



export default Tasks;
